const float pi = 3.141592654;

vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(0.9, 0.9, 0.9);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.yy;

    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 7.0; i++) {

        float r = length(uv);
        float theta = atan(uv.y, uv.x);  

        vec3 col = palette(r + i - sin(iTime*0.3));

        float shape = cos(5.*theta + pi*i + 1./(pow(r, log(iTime + 1.)) + 0.5*i*iTime/100.0) - 1.5*iTime);

        finalColor += col * shape;
    }

    fragColor =  vec4(finalColor, 1.0);
}
