#define oldBuffer iChannel1
#define newBuffer iChannel0
float fadeDuration = 3.0;
float fadeLevel;

#define PI 3.1415

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // This is a uniform in my real program
    fadeLevel = clamp(iTime / fadeDuration, 0.0, 1.0);

	vec2 uv = fragCoord.xy / iResolution.xy;
    float diff = uv.x - uv.y;

    float clock = fadeLevel * PI / 2.0;
    float t = sin(-clock - floor(-clock * 2.0 / PI) * PI / 2.0);

    float offset;

    if(diff < -0.5)
    {
    	offset = -1.0 * t;
    }
    else if(diff > -0.5 && diff < 0.0)
    {
    	offset = 1.0 * t;
    }
    if(diff > 0.0 && diff < 0.5)
    {
        offset = -1.0 * t;
    }
    else if(diff > 0.5 && diff < 1.0)
    {
    	offset = 1.0 * t;
    }

    vec2 pq =  uv + vec2(offset, offset);
    fragColor = (pq.y > 1.0 || pq.y < 0.0 || pq.x > 1.0 || pq.x < 0.0)
        ? texture(oldBuffer, uv)
        : texture(newBuffer, pq);
}
