void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //p is our pixel position in the framebuffer. This line makes the origin at the center
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    //d is the distance from our pixel to the origin
	float d = length(p);

    // d_from_circle is the distance from the circle to the pixel. 0.7 is defining the radius of our circle
    float d_from_circle = d - 0.7;

    //set the pixels color to black to start
    vec3 col = vec3(0.0);

    //if the distance from the pixel to the circle is small, make it white
    if(d_from_circle > -0.1 && d_from_circle < 0.1)
    {
        col = vec3(1.0);
    }

    fragColor = vec4(col,1.0);
}
