void mainImage( out vec4 O, in vec2 n )
{
    float a = iTime * 0.005;
    n = n/iResolution.xy * 4. - 1.;

    for(int i=0; i++<300;){
        n = (abs(n)-0.5)*1.01*mat2(cos(2.*a),-sin(a),sin(2.*a),cos(a));

    }
    // Output to screen
    O = vec4(length(n+vec2(-0.1,0.1)),length(n+vec2(-0.1,0.6)),length(n+vec2(-0.5,0.5)),1.0);
}
