// Author: zokalyx
// Title: patterns exercise
// Comment: just wanted to copy this shader on my own: https://thebookofshaders.com/edit.php#09/marching_dots.frag

float circle (in vec2 _st, in float radius) {
    float dist = length(_st - 0.5);
    return smoothstep(dist, dist + 15.0 / iResolution.y, radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy / iResolution.y;

    st *= 10.0;

    // Only move horizontally if u_time has an odd number of secconds
    // Other case move vertically
    float time_parity = step( mod(iTime, 2.0), 1.0);

    st.x +=        time_parity  * iTime * (2.0 * step( mod(st.y, 2.0), 1.0 ) - 1.0);
    st.y += (1.0 - time_parity) * iTime * (2.0 * step( mod(st.x, 2.0), 1.0 ) - 1.0);

    st = fract(st);

    vec3 color = vec3(1.0 - circle(st, 0.3));

    fragColor = vec4(color, 1.0);
}
