#define MAX_STEPS 100
#define MAX_DIST 250.
#define SURF_DIST .01
#define TAU 6.283185
#define PI 3.141592
#define THETA 0.6

const float globalAmbient = 0.4; // how strong is the ambient lightning
const float globalDiffuse = 0.8; // how strong is the diffuse lightning
const float globalSpecular = 1.0; // how strong is the specular lightning
const float globalSpecularExponent = 32.0; // how focused is the shiny spot
const vec3 lightPos = vec3(0.0, 1.0, -0.5); // position of the light source
const vec3 lightColor = vec3(0.9, 0.9, 0.68); // color of the light source
const vec3 ambientColor = vec3(1.0, 1.0, 1.0); // ambient color

const float sth = sin(THETA);
const float cth = cos(THETA);

const mat3x3 rot_mat = mat3x3(
    cth * sth, sth * sth * cth - cth * sth,cth * sth * cth + sth * sth,
    cth * sth, sth * sth* sth + cth * cth,cth * sth * sth - sth * cth,
    -sth, sth * cth, cth * cth
);

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

vec2 GetDist(vec3 p) {    
    float d = 888.0;
    float mat = 0.0;

    for (float i = 0.0; i <16.0; i++) {
        p *= rot_mat;

        p *= 1.0;

        p = abs(p);
        p -= 0.6;
    }

    d = length(p)-0.3;

    return vec2(d,mat);
}

vec2 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float mat;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        vec2 dS = GetDist(p);
        dO += dS.x;
        mat = dS.y;

        if(dO>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return vec2(dO,mat);
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p).x - 
        vec3(GetDist(p-e.xyy).x, GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);

    return normalize(n);
}

float ambientOcclusion(vec3 p, vec3 n){
    const int steps = 1;
    const float delta = 0.5;

    float a = 0.0;
    float weight = 0.75;
    float m;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta;
        a += weight*(d - GetDist(p + n*d).x);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<100; i++ )
    {
		float h = GetDist( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.1, 1. );
}


vec3 shade(  vec3 position,
                           vec3 lightPosition,
                           vec3 ambientCol,
                           vec3 lightCol,
                           float ambientCoeff,
                           float diffuseCoeff,
                           float specularCoeff,
                           float specularExponent,
                           vec3 camera_pos,
                           vec3 ray_direction
)
{
      vec3 normal = GetNormal(position);
      vec3 toEye = normalize(camera_pos - position);
      vec3 toLight = normalize(lightPosition - position);
      vec3 reflection = reflect(-toLight, normal);

      vec3 ambientFactor = ambientCol * ambientCoeff;
      vec3 diffuseFactor = diffuseCoeff * lightCol * max(0.0, dot(normal, toLight));
      vec3 specularFactor = lightCol * pow(max(0.0, dot(toEye, reflection)), specularExponent)
                     * specularCoeff;

      float bac = clamp( dot( normal, normalize(vec3(-lightPosition.x,0.0,-lightPosition.z))), 0.0, 1.0 )*clamp( 1.0-position.y,0.0,1.0);
      float fre = pow( clamp(1.0+dot(normal,ray_direction),0.0,1.0), 2.0 );

      float ao = ambientOcclusion(position+normal*0.1,normal);
      float ss= softshadow(position,lightPos,0.1,10.0);

    diffuseFactor *= ss;
    diffuseFactor *= ao;
    return ambientFactor + diffuseFactor + specularFactor + 0.30 * bac + 0.20 * fre;
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 0.5);
    vec3 bg = mix(vec3(1.,0.557,0.561),vec3(1.,0.992,0.796),rd.y);
    vec3 col = bg;

    vec2 rm = RayMarch(ro, rd);
    float d = rm.x;

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        if (rm.y == 0.0) {
            col = vec3(0.882,0.471,0.773);
        } else {
            col = vec3(1.,0.557,0.561);
        }
        col *= shade(p,lightPos,ambientColor,lightColor,globalAmbient,globalDiffuse,globalSpecular,globalSpecularExponent,ro,rd);
        col = mix(col,bg,1.0 - exp(-0.0004*d*d));
    }

    float c = 1.1;
    float b = 0.0;
    col = c * (col-0.5) + 0.5 + b;
    vec3 greyscale = vec3(dot(col,vec3(0.299,0.587,0.114)));
    col = mix(greyscale,col,1.3);
    col = clamp(col,vec3(0.0),vec3(1.0));

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
