void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col,z,s,f,m,n;
    vec2 uv = fragCoord/iResolution.xy;
   m=cos(iTime/10.)+1.;
   n=sin(iTime/10.)+1.;
   for(f=0.;f<10.;f++){

    z = (sin(uv.y+0.3)*
        sin(uv.x+0.5))*
       (iResolution.y-fragCoord.y)/(1.+(cos(iTime/3.)*150.+150.)+s*500.);
    uv.x = (((fragCoord.x-iResolution.x/2.)/iResolution.x)/z)+m;
    uv.y = (((fragCoord.y-iResolution.y/2.)/iResolution.y)/z)+n;
    s = float(texture(iChannel0, uv))/5.;  
    col += float(f>0.)/(0.1/abs(s));

   }
   fragColor = vec4(0.6/col,0.8/col,0.8/col,1.);
}
