const vec2 center = vec2(0.5,0.5);

float f(vec2 p) {
  return texture(iChannel0,p + center).r;
}

float g0(vec2 p) {
  return f(p);
}

#define F(g0) 0.80*g0(p) + 0.07*g0(-0.8*p) + 0.25*g0(0.9*p + vec2(0.01,0.02))

float g1(vec2 p) { return F(f); }
float g2(vec2 p) { return F(g1); }
float g3(vec2 p) { return F(g2); }
float g4(vec2 p) { return F(g3); }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy - center;
    float a1 = 13.0;
    float w1 = 2.0;
    float phi1 = 0.3;
    float a2 = 1.5;
    float w2 = 5.0;
    float phi2 = 1.2;
    vec2 u = a1*vec2(cos(w1*iTime + phi1),sin(w1*iTime + phi1));
    u += a2*vec2(cos(w2*iTime + phi2),sin(w2*iTime + phi2));

    float x = 0.5*g4(p);
    float y = texture(iChannel1,0.99*(fragCoord + u)/iResolution.xy).r;
    float alpha = 0.01;
    y += alpha*(x - y);

    fragColor = vec4(y,x,y,1.0);
}
