vec2 brick_tile(vec2 uv, float scale){
    uv *= scale;
    uv.x += step(1., mod(uv.y, 2.0))*0.5;

    return fract(uv);
}

float boxify(vec2 uv, vec2 size) {

    // Converts from 0->1 to 0.5->0
    //  - squash from 0->1 to 0.5
    //  - flip operands by sub with 0.5
    size = 0.5 - size * 0.5; 
    uv = smoothstep(size, size + 0.01, uv);
    return uv.x*uv.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord -iResolution.xy)/iResolution.y;

    uv.y *= 1.5;  
    uv = brick_tile(uv, 5.);
    vec3 col = vec3(boxify(uv, vec2(0.9)));


    fragColor = vec4(col,1.0);
}
