#define grad_a vec3(0.548,0.565,0.542)
#define grad_b vec3(0.295,0.295,0.295)
#define grad_c vec3(0.904,0.947,0.965)

vec3 pillar(in vec2 uv, in float x, out float a) {

    if (abs(uv.x - x) < 0.05 && abs(uv.y - 0.5) < 0.35) {
        a = 1.0;
        return mix(grad_b, grad_c, 1.0-uv.y);
    }

    a = 0.0;
    return vec3(1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = mix(grad_b, grad_c, uv.y);

    float a = 0.0;

    color = mix(color, pillar(uv, 0.25, a), a);
    color = mix(color, pillar(uv, 0.5, a), a);
    color = mix(color, pillar(uv, 0.75, a), a);


    fragColor = vec4(color, 1.0);
}
