void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;

    uv.y += uv.x;
    float c = uv.y + iTime / 2.;
    c = mod(c, 1.);
    c = step(c, 0.4);

    // Calculate the stripe color
    vec3 background = mix(vec3(0., 1., 0.), vec3(0.), c);


    fragColor = vec4(background, 1.0);
}
