const vec3 COL1 = vec3(0.6,0.3,0.4);
const vec3 COL2 = vec3(0.0,0.0,0.2);
const float OFFSET = 1.2;
const float SPEED = 64.0;
const float SCALE = 20.0;
const float FADERANGE = 32.0;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float fragUV = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= fragUV;

    float circle = uv.x - OFFSET;

    fragCoord.x += iTime * SPEED;

	vec2 center = floor(fragCoord/SCALE)*SCALE + SCALE/2.0;

    float l = max(0.01, circle);

    float dist = distance(center,fragCoord)/FADERANGE;

    float alpha = smoothstep(0.9, 1.0, dist/l);

    fragColor.rgb = mix(COL1, COL2, alpha);
}
