// https://www.shadertoy.com/view/Mdc3zr

float tinkeredsmoothstep( vec2 uv, float a, float b ) {
    float c = pow( abs(uv.x), uv.x * a + b );
    return (1.0 - smoothstep( c * uv.y * uv.x, c, uv.y ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy / iResolution.xy;

    float f = tinkeredsmoothstep(uv, 1.0, 3.0 );

    fragColor = vec4(f,f,f,1.0);

}
