// integer domain SRGB Color Matching Functions
vec3 srgbcmfi(
  int l) {
  return l >= 360 && l <= 830 ? srgbcmf2[l - 360] : vec3(0.0); }

// nonlinear sR'G'B' component to linear sRGB component
float srgb_igamma(
  float v) {
  return v <= 0.04045
    ? v * 0.0773993808
    : pow((v + 0.055) * 0.94786729857, 2.4) ; }

vec3 srgb_igamma(
  vec3 v) {
  return vec3(
    srgb_igamma(v.r),
    srgb_igamma(v.g),
    srgb_igamma(v.b)); }

//
// math lib
//

// needed to produce infinities
float zero = 0.0;

const float inv_4pi = 0.07957747154;
const float pi = acos(-1.0);

// vector lib

// handy nil vector
const vec4 nil = vec4(0.0);

// basis vectors
const vec4 dx = vec4(1.0, nil.xyz);
const vec4 dy = dx.wxyz;
const vec4 dz = dy.wxyz;
const vec4 dw = dz.wxyz;

vec2 dir2(
  float a) {
  return vec2(cos(a), sin(a)); }

// transformation matrices

mat4 translate(
  vec4 by) {
  return mat4(dx, dy, dz, dw + by); }

mat4 scale(
  float by) {
  return mat4(by * dx, by * dy, by * dz, dw); }

mat4 rotate(
  vec4 axis,
  float angle) {
  axis = normalize(axis);
  float s = sin(angle);
  float c = cos(angle);
  vec4 l = (1.0 - c) * axis;
  vec3 m = l.xyz * axis.yzx;
  vec4 n = s * axis;
  return mat4(
    l.x * axis.x, m.x - n.z, m.z + n.y, 0.0,
    m.x + n.z, l.y * axis.y, m.y - n.x, 0.0,
    m.z - n.y, m.y + n.x, l.z * axis.z, 0.0,
    nil) + scale(c); }

mat3 rotate(
  vec3 axis,
  float angle) {
  axis = normalize(axis);
  float s = sin(angle);
  float c = cos(angle);
  vec3 l = (1.0 - c) * axis;
  vec3 m = l.xyz * axis.yzx;
  vec3 n = s * axis;
  l *= axis;
  l += c;
  return mat3(
    l.x, m.x - n.z, m.z + n.y,
    m.x + n.z, l.y, m.y - n.x,
    m.z - n.y, m.y + n.x, l.z); }

// vector projection
vec3 projectu(
  vec3 v,
  vec3 u) {
  return u * dot(v, u); }

// vector rejection
vec3 rejectu(
  vec3 v,
  vec3 u) {
  return v - projectu(v, u); }

// pseudorandom

// i can't believe it's an LCG
// https://www.ams.org/journals/mcom/1999-68-225/S0025-5718-99-00996-5/S0025-5718-99-00996-5.pdf
int next_hash(
  int s) {
  return s * 594156893; }

// multilinear congruential seed function
int hash(
  int k,
  int x,
  int y) {
  int s = next_hash(k * 37769685);
  s ^= next_hash(x * 26757677);
  s ^= next_hash(y * 20501397);
  return next_hash(s); }

int hash(
  ivec3 p) {
  return hash(p.x, p.y, p.z); }

int seed;

void srand(ivec3 p) {
  seed = hash(p); }

int rand() {
  seed = next_hash(seed);
  return seed >> 1; }

const float rand_size = pow(2.0, 31.0);

float unitrand() {
  return float(rand() + (1 << 30)) / rand_size; }

float boxrand() {
  return float(rand()) / rand_size; }

const float eps = 1e-5;


// https://www.shadertoy.com/view/lfs3Wn
vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}


void transform(inout vec2 uv, inout vec2 t2){
        t2 = triangle_wave(uv+.5);
        uv =
            t2-triangle_wave(uv.yx)-fract(t2/2.)
        ;
        //{t2.x = (t2.x+1.5*sign(t2.y-t2.x)); }
        //{uv.x = (uv.x+1.5*sign(uv.y-uv.x)); }
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, s, -s, c);
	return m * v;
}

vec3 carpet(vec2 uv){
    vec3 col = vec3(0.);
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    float c1=0.;
    for(int k = 0; k < 15; k++){
        float warp_scale = 16.;
        vec2 warp =
            //abs(.5-fract(uv*3.))*3.
            //abs(.5-fract(t2*3.))*3.
            vec2(sin((t2.x)*warp_scale),cos((t2.y)*warp_scale))
            //vec2(sin((uv.x)*warp_scale),cos((uv.y)*warp_scale))
        ;
        uv.y -= 1./4.;
        float scale = 1.5;

        uv = (uv+t2)/scale;

        uv = (fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;
        col.x =
            max(length(uv-t2-c1)/3.,col.x);

        ;
        if(k>1)
        warp = warp*warp/warp_scale;
        else
        warp = vec2(0);

        vec2 uv_1 =
            uv + warp.yx
        ,
        t2_1=
            t2 + warp.yx
        ;
        vec3 col_1 = col;
        transform(uv,t2);

        transform(uv_1,t2_1);
        //uv_1 = rotate(uv_1,t2.x*2.);
        //t2_1 = rotate(t2_1,t2.x*2.);

        c1 =
            max(abs(uv_1.y+uv_1.x)/2.,c1)
            //max(abs(uv_1.y-uv_1.x),c1)
        ;
        c1 =
            max(1.-abs(2.*c1-1.),c1/4.)
        ;
        col.x =
            max(length(uv_1-t2_1-c1)/3.,col.x)

        ;
        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx
            //abs(col1*c1*sign(t2.y-t2.x)/2.-col-1.).yzx
            //abs(col1*c1-col+sign(t2.x-t2.y)).yzx
        ;
    }
    return col1;
}

// models an interaction with isotropic roughness
// n - the surface normal
// s - the azimuth of the incoming ray
// you should supply a random value between 1.0 and -1.0 for s
vec3 axial(
  vec3 n,
  float v) {
  float u = 6.28318530718 * boxrand();
  float r = sqrt(1.0 - pow(v, 2.0));
  vec3 t = normalize(abs(n.y) < 0.5 ? vec3(-n.z, 0.0, n.x) : vec3(-n.y, n.x, 0.0));
  vec3 b = cross(n.xyz, t.xyz);
  return vec3(v * n.xyz + r * (cos(u) * t + sin(u) * b)); }

vec3 power_icdf(vec3 spec_dir, float power) {
  return axial(spec_dir, pow(unitrand(), 1.0 / (power + 1.0))); }

float power_pdf(
  vec3 spec_dir,
  float power,
  vec3 rd) {
  return (power + 1.0) / (2.0 * pi) * pow(max(0.0, dot(spec_dir, rd)), power); }

vec3 ro;
vec3 rd;
float t;
vec3 p;
vec3 n;
vec3 wl;
vec3 throughput = vec3(1.0);
vec3 Lo = vec3(0.0);

float fresnel_reflectance(float ior) {
  float dt1 = dot(rd, -n);
  float ior2 = pow(ior, 2.0);
  float dt12 = pow(dt1, 2.0);
  float discriminant = -1.0 + ior2 + dt12;
  if (discriminant < 0.0) return 1.0;
  // fresnel reflectance for positive discriminant
  vec3 refracted_heading = normalize(
    rd + (-dt1 + sqrt(discriminant)) * -n);
  float dt2 = dot(refracted_heading, -n);
  float dt22 = pow(dt2, 2.0);
  float k1 = dt12 + ior2 * dt22;
  float k2 = ior2 * dt12 + dt22;
  float v = 2.0 * ior * dt1 * dt2;
  return clamp(0.5 * (
    (k1 - v) / (k1 + v)
    + (k2 - v) / (k2 + v)), 0.0, 1.0); }

void cos_icdf() {
  float v = unitrand();
  float u = 6.28318530718 * boxrand();
  float r = sqrt(1.0 - v);
  vec3 t = normalize(abs(n.y) < 0.5 ? vec3(-n.z, 0.0, n.x) : vec3(-n.y, n.x, 0.0));
  vec3 b = cross(n.xyz, t.xyz);
  rd = vec3(v * n.xyz + r * (cos(u) * t + sin(u) * b)); }

// SRGB Color Matching Functions
vec3 srgbcmf() {
  return
    srgbcmfi(int(wl.x)) * Lo.x +
    srgbcmfi(int(wl.y)) * Lo.y +
    srgbcmfi(int(wl.z)) * Lo.z; }

vec3 quadsorb(
  float c,
  float w) {
  return 1.0 + pow(abs((wl - c) / w), vec3(2.0)); }

vec3 nf() {
  float d = df(p);
  vec2 e = vec2(eps, 0);
  vec3 n = d - vec3(
    df(p - e.xyy),
    df(p - e.yxy),
    df(p - e.yyx));
  return normalize(n); }

void scene() {
  for (int i = 0; i < 256; ++i) {
    float dmin = 1.0 / zero;
    p = ro + t * rd;
    float ddode = 1.0 / zero;
    vec3 H = vec3(0.0, 0.0, 0.0) - p;
    float D = dot(H, H) - pow(20.0, 2.0);
    if (D < 0.0 || dot(H, rd) > sqrt(D)) {
      ddode = df(p);
      dmin = min(ddode, dmin); }
    float dplane = 1.0 / zero;
    if (p.y > -1.5 && rd.y < 0.0) {
      dplane = -(p.y + 1.5) / rd.y;
      dmin = min(dplane, dmin); }
    p = ro + (t + dmin) * rd;
    if (dmin == 1.0 / zero) {
      vec3 room = srgb_igamma(texture(iChannel3, rd).bgr);
      vec3 contrib = room / (1.2 - room);
      Lo += throughput * contrib;
      break; }
    float specularity;
    vec3 albedo;
    vec3 ior;
    if (dmin == dplane) {
      ro = p; t = 0.0;
      n = dy.xyz;
      albedo = srgb_igamma(0.5 * carpet((ro.xz + vec2(0.0, 20.0)) / 400.0).bgr);
      ior = vec3(1.5); }
    if (dmin == vID.z) {
      if (dmin > eps) {
        t += dmin;
        continue; }
      n = nf();
      if (dot(rd, n) > 0.0) {
        t += eps;
        continue; }
      ro = p; t = 0.0;
      n = texBump(iChannel1, ro * 5.0, n, 0.005);
      albedo = 0.5 / quadsorb(700.0, 100.0);
      ior = quadsorb(400.0, 100.0) + vec3(1.7) / quadsorb(750.0, 300.0); }
     if (dmin == vID.w) {
      if (dmin > eps) {
        t += dmin;
        continue; }
      n = nf();
      if (dot(rd, n) > 0.0) {
        t += eps;
        continue; }
      ro = p; t = 0.0;
      n = texBump(iChannel1, ro * 5.0, n, 0.005);
      albedo = vec3(1.0);
      ior = vec3(2.5); }
    float refl = 0.5;
    if (unitrand() < refl) {
      throughput.x *= fresnel_reflectance(ior.x) / refl;
      throughput.y *= fresnel_reflectance(ior.y) / refl;
      throughput.z *= fresnel_reflectance(ior.z) / refl;
      rd = reflect(rd, n); }
    else {
      throughput.x *= (1.0 - fresnel_reflectance(ior.x)) / (1.0 - refl);
      throughput.y *= (1.0 - fresnel_reflectance(ior.y)) / (1.0 - refl);
      throughput.z *= (1.0 - fresnel_reflectance(ior.z)) / (1.0 - refl);
      throughput *= albedo;
      cos_icdf(); }  } }

#define INTERACT 1

void mainImage(out vec4 o, in vec2 xy) {
  srand(ivec3(xy, iFrame + int(iFrameRate * iTime)));

  float wl_min = 360.0;
  float wl_max = 800.0;
  float wl_rng = wl_max - wl_min;
  vec2 wl_pts = wl_min + vec2(1.0, 2.0) * wl_rng / 3.0;
  wl = mix(
    vec3(wl_min, wl_pts.x, wl_pts.y),
    vec3(wl_pts.x, wl_pts.y, wl_max),
    vec3(unitrand(), unitrand(), unitrand()));
  mat4 view =
    rotate(dy, -1.3
#if INTERACT
      - 3.0 * pi * (iMouse.x / iResolution.x)
#endif
      ) *
    rotate(dx, -0.1
#if INTERACT
      + 3.0 * pi * (iMouse.y / iResolution.y)
#endif
    ) *
    translate(vec4(0.0, 0.0, -8.5, 0.0));
  vec2 lenspoint = 0.01 * sqrt(unitrand()) 
    * dir2(6.28318530718 * boxrand());
  ro = vec3(lenspoint, 0.0);
  rd = normalize(vec3(
    (xy - 0.5 * iResolution.xy + vec2(boxrand(), boxrand()))
    / (iResolution.y * 2.6)
    - (lenspoint / 7.5),
    1.0));
  ro = (view * vec4(ro, 1.0)).xyz;
  rd = (view * vec4(rd, 0.0)).xyz;
  t = 0.0;
  scene();
  o = vec4(srgbcmf(), 1.0);
  if (true
#if INTERACT
    && iMouse.z <= 0.0
#endif
    ) {
    o += texelFetch(iChannel0, ivec2(xy), 0); }
}
