vec3 palette(float t) {
    vec3 a = vec3(0.3, 0.5, 0.7);
    vec3 b = vec3(0.8, 0.4, 0.6);
    vec3 c = vec3(0.7, 0.8, 0.5);
    vec3 d = vec3(0.6, 0.2, 0.9);
    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fract((fragCoord * 2.0 - iResolution.xy) / iResolution.y * 3.0) - 0.5;
    vec3 finalColor = vec3(0.0);
    for (float i = 0.0; i < 3.0; i++) {
        uv = reflect(uv, vec2(0.7, -0.7));
        float d = length(uv);
        vec3 col = palette(d + i * 0.5 + iTime * 0.2);
        finalColor += col * pow(abs(sin(d * 12.0 + iTime)), 1.3);
    }
    fragColor = vec4(finalColor, 1.0);
}
