float newlength(in vec2 p)
{
    return abs(p.x) + abs(p.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    uv = fract(uv * 2.)- 0.5;

    float d = mod(-newlength(uv), (newlength(uv0)));

    d = abs((sin(d * 4. + iTime)))/8.;
    if (d >= 0.1) {
        d = 1.;
    }

    fragColor = vec4(fract(d * .5)*1.3 , newlength(uv0) * d / 7., d * newlength(uv), 1.0);
}
