void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 col = texture(iChannel0, uv);
    fragColor = vec4(col.rgb, 1.);
}

vec3 palette( float t, float fft ) {
    float _t = sin(t+fft*1.5)*0.5;
    float _T = 128.*(0.5 + 0.5*sin(t*0.1));

    vec3 a = vec3(0.1, 0.1, 0.1);
    vec3 b = vec3(0.5+_t, 0.5-_t, 0.5+_t);
    vec3 c = vec3(0.5-_t, 0.5+_t, 0.5-_t);
    vec3 d = vec3(0.5+_t,0.116,0.157);

    return a + b*cos( 6.28318*(c*_t*_T+d) );
}

vec2 barrelDistortion(vec2 coord, float amt) {
	vec2 cc = (coord - 0.5);
	float dist = dot(cc, cc);
	return coord + cc * (dist) * amt;
}


mat2 rot(float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fft  = texelFetch( iChannel1, ivec2(0.1, 0.1), 0 ).x;
    float speed = -0.5;
    float t=iTime*speed;


    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = barrelDistortion(uv, -0.2);
    vec2 _uv = uv;
    uv -= 0.5;
    uv.x *= aspect;


    // warp and turn
    float scale = 1.2726;   //7.//5.41//3.5;//2.545;//1.91;//1.2726;//.6365;
    uv *= 0.5*sin(t*0.1)*rot(1.*sin(t+mix(log(length(uv)),length(uv), sin(t))));
    uv *= rot(.5*t+length(uv));
    uv = sin(t*0.1)*vec2(log(length(uv)+0.25*(0.5+sin(t)*0.5)), atan(uv.y, uv.x))*scale;
    uv.x -= 0.5*t;


    uv = fract(uv)-0.5;


    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++) {


    uv *= 0.5*sin(t*0.1)*rot(1.*sin(t+mix(log(length(uv)),length(uv), sin(t))));
    uv *= rot(.5*t+length(uv));
    uv = sin(t*0.1)*vec2(log(length(uv)+0.25*(0.5+sin(t)*0.5)), atan(uv.y, uv.x))*scale;
    uv.x -= 0.5*t;


    uv = fract(uv)-0.5;


        float d = pow(length(uv0),i+(1.+sin(0.5*t))) * exp(length(uv));

        vec3 col = palette(length(uv0)+i+t*0.2, fft);

        d = sin(d*8. - t)/32.;


        if(d != 0.0){
            d = abs(d);
            d = pow(0.01/ d, 0.9);
        }


        finalColor += col * d;
    }


    vec3 old = texture(iChannel0, _uv).rgb;
    fragColor = vec4(
        mix(
            old,
            finalColor,
            0.5
        )
    , 1.0);
}
