float hash21(vec2 p) {
    p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv *= 10.;

    vec2 gv = fract(uv)-0.5;
    vec2 id = floor(uv);

    float n = hash21(id);
    float t = round(iTime / 2.0);
    float nt = hash21(vec2(t * id));
    if (nt > 0.995) n = 1.0 - n;

    if        (n > 0.75) {
        gv.x *= -1.0;
        gv.y *= -1.0;
    } else if (n > 0.50) {
        gv.x *=  1.0;
        gv.y *= -1.0;
    } else if (n > 0.25) {
        gv.x *= -1.0;
        gv.y *=  1.0;
    } else               {
        gv.x *=  1.0;
        gv.y *=  1.0;
    }
    float mask = smoothstep(0.01, -0.01, gv.x - gv.y);
    float val = sin(sqrt(uv.x*uv.x+uv.y*uv.y)*2.0 + iTime*2.0)* 0.25 + 0.75;
    mask *= n * val;

    vec3 col = vec3(0.0);
    col += mask;

    if (length(col) <= 0.01) {
        col += 0.4;
        col *= vec3(0.6, 0.3, 1.0);
    }

    col *= vec3(1.0 - (1.0 - val), 1.0 - val, 1.0 - val);



    fragColor = vec4(col,1.0);
}
