void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;
    float squareSize = 0.1 * resolution.x;
    float time = iTime;
    vec2 circle1 = vec2(0.6 + 0.3 * cos(time), 0.6 + 0.3 * sin(time)); 
    vec2 circle2 = vec2(0.6 + 0.25 * cos(time), 0.6 + 0.25 * sin(time)); 
    vec2 square = vec2(0.2, 0.9);
    vec3 color1 = vec3(0.8, 0.8, .8); 
    vec3 color2 = vec3(.9, .9, .9);
    vec3 color3 = vec3(.1, .1, .1); 
    vec3 backgroundColor = 0.2 + 0.1 * tan(50.*time + fragCoord.xyx * 0.2 + length(fragCoord.yx) * 0.6);
    float distance3 = length(fragCoord - resolution * square);
    float distance1 = length(fragCoord - resolution * circle1);  
    float distance2 = length(fragCoord - resolution * circle2);
    float circleRadius = .2 * resolution.x;
    vec3 finalColor = backgroundColor;  
    if (distance1 < circleRadius){
    finalColor = color1;}
    if (distance2 < circleRadius) {
        finalColor = color2;}
    if (abs(fragCoord.x - resolution.x * square.x) < squareSize && abs(fragCoord.y - resolution.y * square.y) < squareSize) {
        finalColor = color3;}
    fragColor = vec4(finalColor, 5.0);
}
