const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
const int KEY_SPACE = 0;

const float dt = 0.01;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    vec2 st = fragCoord/iResolution.x;

#if SET==0
    st *= 50.0;
#elif SET==1
    st *= 1.0;
#endif

    vec2 vel = velocityfield(st,0.1*iTime);
    float s = 0.99;

#if SET==0
    s = 0.99;
#elif SET==1
    s = sqrt(vel.x*vel.x + vel.y*vel.y);
    s = 1.0 - s*s;
    vel *= 0.8;
#endif


    fragColor = s*texture(iChannel0,uv/iResolution.xy - vel*dt) + (1.0-s)*texture(iChannel2,uv/iResolution.x - 0.001*iTime*vel);

    if(texelFetch(iChannel1, ivec2(KEY_UP, 0), 0).r == 1.0) {
        fragColor = texture(iChannel2,uv/iResolution.x);
    }
}

