vec2 noise2(in vec2 _st) {
    return vec2(noise(_st),noise(vec2(12.9898,78.233)+_st));
}

vec2 fbm2(in vec2 _st) {
    vec2 v = vec2(0.0);
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise2(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

vec2 velocityfield(in vec2 _st, in float t) {
    float mult1 = 1.0;
#if SET==0
    mult1 = 1.0;
#else 
    mult1 = 3.0;
#endif
    return fbm2(mult1*fbm2(3.0*_st) + vec2(33.344,12.345) + vec2(0.1,0.8)*t) - vec2(0.5);
}
