vec2 mdb( vec2 z, vec2 c ) {
    // complex number z = vec2(x real, y imag)

    // Square real and imaginary components of z, add c
    return vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from -scale to scale)
    float scale = 2.5;
    vec2 uv = (fragCoord/iResolution.xy - 0.5)*scale;
    uv.x *= iResolution.x/iResolution.y;

    vec2 z0 = vec2(0.);

    float lum = 0.;

    while (length(z0) < 3.) {
        z0 = mdb(z0, uv);
        lum += 0.02;
        if (lum >= 1.) break;
    }

    vec3 col = hsv2rgb(vec3(lum, 1., 1.));

    // Output to screen
    fragColor = vec4(col,1.0);
}
