void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Diagonal coordinates
    float diagonal = uv.x + uv.y;

    float time = iTime * 0.5;

    float distortion = sin(diagonal * 0.4 + time) * 0.1;

    // Wave animation
    float wave = sin(diagonal * 10.0 + time) * 0.1;

    // Black color 
    vec3 blackColor = vec3(0.0, 0.0, 0.0);

    // Green color 
    vec3 greenColor = vec3(0.0, 0.3, 0.0); 

    // Blend between black and green 
    vec3 waveColor = mix(greenColor, blackColor, abs(wave));

    vec3 col = waveColor + vec3(distortion, 0.0, distortion);

    fragColor = vec4(col, 1.0);
}
