#define PI 3.14159265359

float discretize(float n, float k) { return floor(n * k) / k; }

// fx: effect index, n: total number of effects
bool transition(float fx, float n) {    
    float d = 1.0; // duration of the effect in seconds
    float d1 = d * n; // Beginning time of the animation in seconds

    return mod(iTime, d1) >= (d * fx) && mod(iTime, d1) < (d * (fx + 1.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 1.5;
    uv -= 0.25;

    uv = vec2(discretize(uv.x, 160.0), discretize(uv.y, 100.0));

    float t = iTime;
    float k = 5.0; // amount of degrees to rotate/warp/distort
    float b = (sin(iTime * PI) * k) / 180.0 * PI; 
    float a = 5.0;

    float n = 2.0;
    if (transition(0.0, n)) {
       uv = vec2(uv.x + cos(uv.y * a + t) * b, uv.y + sin(uv.x * a + t) * b);
    } else if (transition(1.0, n)) {
       uv = vec2(uv.x + sin(uv.x * a + t) * b, uv.y + sin(uv.x * a + t) * b);
    }

    if (uv.x < 0.0 || uv.x > 1.0 ||
        uv.y < 0.0 || uv.y > 1.0) {
       fragColor = vec4(vec3(0), 1.0);
       return;
    }


    fragColor = textureLod(iChannel0, uv, 0.0);
    fragColor = floor(fragColor * 4.0) / 4.0;
}
