float fmod(float t,float a){
  return fract(t/a)*a;
}

#define N 7.0

// shuffling parts of the image

vec2 f(vec2 v){
    vec2 ind = floor(v*N);
    float i = floor( ind.y * N + ind.x);
    i = fmod ((0.2*iTime)* i,N*N);
    ind.x =fmod( i,N);
    ind.y = floor(i/N);
    return vec2(ind.x/N+fmod(v.x,1.0/N),ind.y/N+fmod(v.y,1.0/N));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 v = fragCoord/iResolution.xy;


    // sample texture and output to screen
    fragColor = texture(iChannel0,f(v));
}
