void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //normalizes and fixes stretching
    vec2 uv = ((fragCoord/iResolution.xy)-.5)*2.;

    //gets the abs of sin by y+iTime and then multiplies all of it by 1.4 to make it scroll and appear even
    uv.x = abs(sin(uv.y+(iTime*2.)));

    //makes colors not foggy and icky
    uv.x = step(.7,uv.x);

    //makes lines red and blue
    fragColor = vec4(-uv.x + 1.,-uv.x,uv.x,1.);
}
