void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.08,0.4,0.01);


    vec2 fractUv = uv;
    fractUv.x = fractUv.x * iResolution.x/iResolution.y;
    fractUv *= 10.;
fractUv = fract(fractUv);
    //uv*=10.;

   vec2 p=(fractUv-.5);




    vec4 col2 = vec4(col, 1.0);
    //vec4 col2 =vec4(1,1,1,1);


    float dis=max(abs(p.x),abs(p.y));
    float t1=0.75;float t2=0.25;
    col2 *= smoothstep(t1, t2, dis);

    // Output to screen
    fragColor = vec4(col2);
}
