void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float mosaicRadius = 20.0;

    vec2 mosaicCoord = floor(fragCoord / mosaicRadius) * mosaicRadius;
    vec2 uv = mosaicCoord/iResolution.xy;
	fragColor = texture(iChannel0, uv);
    //vec4 beforeColor = texture(iChannel0, 1.0 - uv);
    vec4 beforeColor = texture(iChannel0, uv * 0.9);

    vec2 pixelOffset = fragCoord - mosaicCoord;
    vec2 center = vec2(mosaicRadius / 2.0);
    vec2 _uv = vec2(length(center - pixelOffset), atan(center.x - pixelOffset.x, center.y - pixelOffset.y) / 6.28);

    float y = _uv.y * 7.;
    float m = min(fract(y),fract(1. - y));
    float c = smoothstep(-2.0, 0., m*10.0 + 2.0 -_uv.x);
    fragColor = mix(beforeColor, fragColor, 1.0 - c);

}
