float hillFunction(float x, float x0, float a){
    float d = x - x0;
    return 1. / (1. + (a * d * d));
}

float trigHill(float x, float x0, float a){
    float d = x - x0;
    return 1. - tanh(a * d * d);
}

float trigStep(float x, float x0, float a){
    return (1. - tanh(a * (x - x0)));
}

vec4 triPoint(vec4 x1, vec4 x2, vec4 x3, float t){
    float thalf = .5 - t;
    float t1 = 1. - t;
    return (x1 * t1 * thalf * 2.)
    + (x2 * t1 * t * 4.)
    + (-x3 * thalf * t * 2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0.);

    float speed = .13;


    vec2 p = (uv * vec2(1., .1)) + vec2(0., -iTime * speed);

    vec4 initCol = texture(iChannel0, p);

    vec4 shift = texture(iChannel1, uv * initCol.xy);

    p.x += shift.x * .05;


    col = texture(iChannel0, p);
    col = vec4(length(col.rgb));



    col *= col;

    float fade = (trigHill(uv.y, .2 * col.r, 10.) + trigStep(uv.y, 0., 3.));

    col *= fade;




    float f = (1. + sin(iTime * .8)) * .5;


    vec4 c1 = 1.4 * normalize(vec4(.6 + f, .75, 1. - f, 1.));
    c1 = triPoint(
        vec4(.75, 1., .5, .1),
        vec4(1., .75, .5, 1.),
        vec4(.5, .75, 1., 1.), 
        f);

    vec4 c2 = c1 * c1;

    vec4 fireColor = mix(c2, c1, col.r);
    col *= fireColor;

    fragColor = col;
}
