void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.y;

    uv = uv * 2.0;
    uv.y -= 1.0;
    uv.x -= iResolution.x/iResolution.y;

    float t = iTime / 2.;
    //float t = iTime;

    t = (1.0-step(0.75,t)+step(1.25,t)) * min(t,1.0) + (step(0.75,t)-step(1.25,t)) * (1.0-pow((t-1.25),2.0));

    mat2 mat = //mat2(1. , 0. , 
               //     -min(t,1.) , 1.)
               //*
               mat2(1. , min(t,1.) ,
                    -min(t,1.) , 1.);

    //mat = mat2(1., sin(t), 
    //          -sin(t), 1.);

    uv *= mat;

    uv = fract(uv);

    fragColor=vec4(vec3(uv.x,uv.y,0.0),1.0);
    //fragColor=vec4(texture(iChannel0,uv).xyz, 1.);
}
