const float x0 = -50.0;
const float x1 =  50.0;
const float y0 =  -2.2;
const float y1 =   2.2;

float safe_tanh(float a) {
    return tanh(clamp(a, -40.0,  40.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv * (vec2(x1, y1) - vec2(x0, y0)) + vec2(x0, y0);

    bool alternate = int(iTime) / 2 % 2 == 0;

    float y;
    if (alternate) {
        y = safe_tanh(p.x);
    }
    else {
        y = tanh(p.x);
    }

    float c = p.y > y ? 0.5 : 0.0;

    float r = isnan(y) ? 1.0 : c;

    fragColor = vec4(r, c, c, 1.0);

    fragColor = mix(fragColor, vec4(0.9), smoothstep(4.0/iResolution.y, 0.0, abs(p.y)));
    fragColor = mix(fragColor, vec4(0.9), smoothstep(200.0/iResolution.x, 0.0, abs(p.x)));
}
