void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 pixelPos = fragCoord + vec2(sin(iTimeDelta), cos(iTimeDelta));

    float time = iTime;

    float pattern = sin(uv.x * 25.0 + time * 0.5 * cos(uv.y * 0.005 + time * 0.5)) * 2.0;

    uv += pattern * 0.1 * sin(time);

    float blob1 = smoothstep( 0.5, 0.52, length(uv - vec2(0.5, 0.5)) );
    float blob2 = smoothstep( 0.47, 0.48, length(uv - vec2(0.5, 0.5)) );
