    float blob7 = smoothstep( 0.2, 0.21111, length(uv - vec2(0.5, 0.5)) );
    float blob8 = smoothstep( 0.15, 0.18, length(uv - vec2(0.5, 0.5)) );
    float blob9 = smoothstep( 0.1, 0.11111, length(uv - vec2(0.5, 0.5)) );

    vec3 color = mix(vec3(0.04706, 0.05882, 0.03922), vec3(1.0, 0.12549, 0.43137), blob9);
    color = mix(color, vec3(0.98431, 1.0, 0.07059), blob8);
    color = mix(color, vec3(0.25490, 0.91765, 0.83137), blob7);
    color = mix(color, vec3(1.0, 1.0, 1.0), blob6);
    color = mix(color, vec3(0.04706, 0.05882, 0.03922), blob5);
    color = mix(color, vec3(1.0, 0.12549, 0.43137), blob4);
    color = mix(color, vec3(0.98431, 1.0, 0.07059), blob3);
    color = mix(color, vec3(0.25490, 0.91765, 0.83137), blob2);
    color = mix(color, vec3(1.0, 1.0, 1.0), blob1);

    fragColor = vec4(color, 1.0);
}
