void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = uv*2.0 - 1.0;

    vec2 suv = cuv * cuv;
    float v = (suv.y + suv.x);

    float d = (0.5 - (0.5 - v))*2.0;

    d = 1.0 - smoothstep(0.97, 1.0, d);

    fragColor = vec4(1,1,1,1) * d;
}
