void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    vec2 center = iResolution.xy / (2.0 * iResolution.y);
    float widthish = 1.0 / iResolution.y;

    float magnitude = distance(uv, center);

    float s = 0.62831853071795860;

    vec2 from_center = (center - uv) / magnitude;
    float angle = atan(from_center.y, from_center.x) + 3.14159 +
        sin(magnitude * 5.0 + iTime) * sin(iTime / 2.0);

    float mod_angle = mod(angle, s);

    fragColor = vec4(0.396, 0.294, 0.737, 1.0);

    float x = mod_angle / (s * 0.5);
    float g = sqrt(pow(magnitude, 1.5));

    float t = max(0.0,
        min(40.0 * x * g,

        min(float(mod_angle < s * 0.5),

        40.0 * (1.0 - x) * g))
    );


    fragColor = mix(fragColor, vec4(1.0, 1.0, 1.0, 1.0), t);
}
