void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Quarter Note Loop Length
    float totalTime = 1./BPM*60.;

    // Loop the envelope for demonstration purposes
    float loopTime = mod(iTime, totalTime)/totalTime;

    // Calculate the envelope value
    float envelope = adsr(loopTime, attack, decay, sustain, release);

    // Use the envelope to modulate the color
    vec3 color = vec3(envelope);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

