vec2 mainSound( int samp, float time )
{

    // Length of a Quarter Note Loop
    float quarterTime = 1./BPM*60.;

    // Loop the envelope for the length of a quarter note
    float loopTime = mod(time, quarterTime)/quarterTime;

    // Create an envelope using the common ADSR variables created that loops
    float envelope = adsr(loopTime, attack, decay, sustain, release);

    // Set frequency of oscillator
    float F = 80.;

    // Return stereo signal
    return UNITY*vec2(envelope*sin(TAU*F*time));

}

//https://www.desmos.com/calculator/qo1k0wk5bo

const float PI = 3.1415926535;
const float TAU = 2.*PI;
const float UNITY = sqrt(.5);
const float BPM = 120.00;

// Envelope parameters
const float attack = .5;
const float decay = 1.;
const float sustain = .0;
const float release = .1;

// ADSR Envelope function
float adsr(float time, float attack, float decay, float sustain, float release) {

    float level;

    if (time < attack) {
        // Attack phase: Linear rise from 0 to 1
        level = time / attack;

    } else if (time < attack + decay) {
        // Decay phase: Linear fall from 1 to sustain level
        level = 1.0 + (sustain - 1.0) * (time - attack) / decay;

    } else if (time < attack + decay + sustain) {
        // Sustain phase: Maintain sustain level
        level = sustain;

    } else if (time < attack + decay + sustain + release) {
        // Release phase: Linear fall from sustain level to 0
        level = sustain * (1.0 - (time - attack - decay - sustain) / release);

    } else {
        // After release: Return to 0
        level = 0.0;
    }

    return level;
}
