float col,x,y,t,w,h;
float a=10.3;
float z=5.;
int f;
int[] i = int[](0xffffffff,0x80000000,0x8e000000,0x8b318e00,0x8b4acd00,0x8b72cc00,0x8e39ec00,0x80000000,0x87000000,0x88319c60,0x866a5a90,0x83639ae0,0x8e39da74,0x80000000,0x80000000,0x8f001c30,0x8b38087c,0x8f7c080c,0x8b540818,0x8b541c70,0x800000c0,0x808c6000,0x81d6b180,0x8096b000,0x80cc61e0,0x80000370,0xa0000660,0xa0c460e0,0xa16e9070,0xa164e020,0xbcf6700c,0x80000000);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    for (f=0;f<100;f++){ //intel HD Graphics can go to 30ish

    a = sin(cos(iTime/118.)-iTime/120.)*10.;
    w = iResolution.x;
    h = iResolution.y;
    t = w/2.+(fragCoord.x+fragCoord.y*iResolution.x);
    z = ((iResolution.y/2.-fragCoord.y)+(sin(iTime/2.)*100.+300.))/((sin(iTime/16.)*40.+42.)-(float(f)/(100.-(sin(iTime*3.)*95.))));

    x = sin(a     )*(mod(t,w)-w/2.)/z +
        cos(a     )*(mod(t/w,w)+500.)/z +iTime*8. ;

    y = cos(a     )*(mod(t,w)-w/2.)/z +
        sin(a+3.14)*(mod(t/w,w)+500.)/z +iTime*5.; 

     col += (float((
                    i[(int(y)&31)]<<(int(x)&31)>>31
                    )&1))*(z/500.); //the 500 must be lowered if f is.
     }



    // Output to screen
    fragColor = vec4(col/1.5,col/5.,col,0.)/2.;
}
