// i can't believe it's an LCG
// https://www.ams.org/journals/mcom/1999-68-225/S0025-5718-99-00996-5/S0025-5718-99-00996-5.pdf
uint seed;

float unitrand() {
  return pow(2.0, -31.0) * float((seed *= 594156893u) >> 1); }

void mainImage(out vec4 o, in vec2 xy) {
  uvec3 p = uvec3(37769685u, 26757677u, 20501397u) * uvec3(xy, iFrame);
  seed = p.x ^ p.y ^ p.z;
  o = vec4(unitrand(), unitrand(), unitrand(), 1.0); }
