/*
    Original => now
    less rotation precision

    rotation mat2( cos(a), sin(a), -sin(a), cos(a) ) => mat2( cos( a + vec4(0,11,33,0) ) )
    HalfPI 1.5707963265 => 1.57

*/

#define r( a ) mat2( cos( a + vec4(0,11,33,0) ) )
#define g( a ) sin( a * 1.5 ) / .9554

float l( vec2 u ){

    u = ( u - vec2( .3 * round( u.x / .3 ), round( u.x / -.3 ) * .1 ) ) * r( g( iTime ) * 1.57 );

    return min(
        max( abs(u.x) - .05, abs(u.y) - .15 ),
        max( abs(u.x) - .15, abs(u.y) - .05 )
    );
}

void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = V * 2. / iResolution.y;

    float p = .1, i;

    if( g( iTime ) > .0 )
        u += vec2( .6, .3 );

    for( ; i < 19.; p = min(
            p,
            l( u - vec2( .1, .3 ) * i++ )
        ) 
    )

    U = vec4( step( 0., p * sign( g( iTime ) ) ) );
}
