Main {
    Q = vec4(.66);
    Q = .7*A(U).w*sin(-2.+A(U).z*4.+vec4(1,2,3,4));
    Q += B(U).w;
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.w-w.w,n.w-s.w,1.));
    vec3 r = reflect(vec3(0,0,1),no);
    Q = 1.-Q; 
    Q *= .8+.5*texture(iChannel2,r);
    Q = 1.-Q;
    Q *= 1.5;
 }

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)

#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)

#define vel(v) (v)*inversesqrt(1.+dot(v,v))

#define inside(v) ((v).x>=0.&&(v).y>=0.&&(v).x<R.x&&(v).y<R.y)

float ln (vec3 p, vec3 a, vec3 b) { 
    return length(p-a-(b-a)*(dot(p-a,b-a)/dot(b-a,b-a)));
}

Main {

    Q = A(U);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (x*x+y*y<=4)
    if (x!=0||y!=0) {
        vec2 v = vec2(x,y);
        vec4 q = A(U+v);
        vec4 b = B(U+v);
        Q.xy += .02*(q.w*(.8-q.w-2.*b.w))*v/dot(v,v);
    }
    Q.y -= .03/R.y;
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy *= 0.;
    if (iFrame < 1) {
        Q.z = .2;
        if (U.y < .5*R.y) Q.w = .8;

    }
    if (iMouse.z>0.&&length(U-iMouse.xy)< 10.) Q.w = 1.,Q.x = .5,Q.z=.8;
}

Main {

Q = A(U);
vec4 dQ = vec4(0);
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (true) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5+.5*clamp(Q.w-q.w,0.15,1.);
        float k = .5+.5*clamp(q.w-Q.w,0.15,1.);
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
    } else {
        dQ += K*Q.w*vec4(Q.xyz,1);
    }

}
if (dQ.w>0.) dQ.xyz /= dQ.w;
Q = dQ;
}

