void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord.xy - iResolution.xy * 0.5;
  mat3 transform = mat3(
    cos(iTime), -sin(iTime), 0.0,
    sin(iTime), cos(iTime), 0.0,
    0.0, 0.0, 1.0
  );
  uv = (vec3(uv, 1.0) * transform).xy;
  ivec2 iuv = ivec2(uv * mat2(1.0, -0.5, 0.0, 1.0) * pow(2.0, fract(-iTime)));
  fragColor.xyz = vec3((iuv.x > 0 || iuv.y > 0) && (iuv.x & iuv.y) <= 1) * vec3(cos(uv.x / iResolution.x * 10.0), 1.0, 1.0);
  fragColor.w = 1.0;
}
