float hash12(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float hash13(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 hash33(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float box(vec2 p, vec2 b) // https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float dot2(vec3 v)
{
    return dot(v,v);
}

// https://www.shadertoy.com/view/3tjczm Bilinear Patch - distance by iq
vec3 bilinear_patch(vec3 p, vec3 p0, vec3 p1, vec3 p2, vec3 p3)
{    
    vec3 A = p1-p0;
    vec3 B = p3-p0;
    vec3 C = p2-p3-p1+p0;
    vec3 D = p0-p;

    // initial guess
    vec2 uv = vec2(0.0,0.0);
    float d = dot2(p-p0);
    for( int i=0; i<16; i++ )
    {
        float u = float(i)/15.0;
        vec3 ba = mix( B,p2-p1,u);
        vec3 pa = mix(-D,p -p1,u);
		float v = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
		float t = dot2(pa-ba*v);
        if( t<d ) { d=t; uv=vec2(u,v); }
    }

    // find roots
    float AA = dot(A,A);
    float BB = dot(B,B);
    float CC = dot(C,C);
    float BC = dot(B,C);
    float AC = dot(A,C);
    float ABCD = dot(A,B)+dot(C,D);
    float AD = dot(A,D);
    float BD = dot(B,D);
    for( int i=0; i<6; i++ )
    {
        vec2 gra = vec2(
         uv.x*AA + uv.x*uv.y*uv.y*CC + uv.y*ABCD + 2.0*uv.x*uv.y*AC +     uv.y*uv.y*BC + AD,
         uv.y*BB + uv.x*uv.x*uv.y*CC + uv.x*ABCD +     uv.x*uv.x*AC + 2.0*uv.x*uv.y*BC + BD);

        float k1 = 2.0*uv.x*uv.y*CC + ABCD + 2.0*uv.x*AC + 2.0*uv.y*BC;
        mat2x2 hes = mat2x2(
         AA + uv.y*uv.y*CC + 2.0*uv.y*AC,
         k1, k1, 
         BB + uv.x*uv.x*CC + 2.0*uv.x*BC );

        uv -= inverse(hes)*gra;

        uv = clamp(uv,0.0,1.0);
    }
    vec3 nor = cross(A+uv.y*C,B+uv.x*C); // normal
	return vec3( length(D+uv.x*A+uv.y*B+uv.x*uv.y*C), uv );
}

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 200u

struct result
{
    vec3 color;
    float dist;
    bool specular;
};

float wave(vec2 p, vec2 f)
{
    return sin(2.0*pi*p.x/f.x)*cos(2.0*pi*p.y/f.y);
}

float height(vec2 p)
{
    float l = length(p);
    //return wave(p, vec2(40,40)) + wave(p, vec2(20,40));
    return 1.0*(0.5*cos(-2.0*pi*l/10.0)+wave(p, vec2(20,20)));
    //return texture(iChannel0, p*0.01).x - 0.5;
}

result terrain_approximation_with_tiles(ivec2 v, vec3 l) // https://www.shadertoy.com/view/4cBfRR terrain approximation with tiles (jt)
{
    vec2 p = vec2(v)/*+0.5*/;
    float h = height(p);
    float e = 0.001;
    vec3 o = vec3(-e,+e,0);
    float s = 2.0;
    vec3 n =
        normalize
        (
            vec3
            (
                (height(p+o.xz)-height(p+o.yz)),
                (height(p+o.zx)-height(p+o.zy)),
                2.0*e/s
            )
        );

    //float d = dot(n, l-vec3(0.5,0.5,h*s)); // plane
    float d =
        bilinear_patch
        (
            l,
            vec3(0,0, height(vec2(v+ivec2(0,0)))),
            vec3(0,1, height(vec2(v+ivec2(0,1)))),
            vec3(1,1, height(vec2(v+ivec2(1,1)))),
            vec3(1,0, height(vec2(v+ivec2(1,0))))
        ).x;
    //vec3 color = hash32(vec2(v));
    //vec3 color = vec3(hash12(vec2(v)));
    vec3 color = vec3(1);
    if(d >= 0.0) return result(color,d-0.1, false);
    return result(vec3(1), box(l.xy-0.5,vec2(0.5)), false); // fill
    //return result(vec3(0), DIST_MAX); // signal to skip this voxel
}

result map(ivec2 v, vec3 l) // unit grid: voxel = float(position), local = fract(position)
{
    return terrain_approximation_with_tiles(v, l);
}
#ifdef HIGH_QUALITY
result map(vec3 p) // for correct ambient occlusion sample 2x2x2 voxels (slow!)
{
    // I think kastorp originally suggested to sample only 2x2x2 instead of 3x3x3, thanks!
    result d = result(vec3(0), DIST_MAX, false);
    ivec2 s = ivec2(step(0.5, fract(p.xy)));
    ivec2 o;
    for(o.y = s.y-1; o.y < s.y+1; o.y++)
        for(o.x = s.x-1; o.x < s.x+1; o.x++)
        {
            result r = map(ivec2(floor(p.xy))+o, vec3(fract(p.xy)-vec2(o),p.z));
            if(r.dist < d.dist)
                d = r;
        }
    return d;
}
#else
result map(vec3 p)
{
    return map(ivec2(floor(p.xy)), vec3(fract(p.xy),p.z));
}
#endif
// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize(k.xyy*map(p + k.xyy*h).dist +
                     k.yyx*map(p + k.yyx*h).dist +
                     k.yxy*map(p + k.yxy*h).dist +
                     k.xxx*map(p + k.xxx*h).dist);
}

// NOTE: Apparently sign fails on some systems! Thanks to spalmer for debugging this!
vec2 sgn(vec2 v) // WORKAROUND FOR COMPILER ERROR on some systems
{
    return step(vec2(0), v) * 2.0 - 1.0;
}

result trace(vec3 ro, vec3 rd, float t0, float t1) // ray-march sdf handling discontinuities between voxels  (jt)
{
    result h;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++) // finite loop originally suggested by pyBlob to avoid stalling if ray parallel to surface just above EPSILON
    {
        vec3 p = ro + rd * t;
        h = map(p);
        if(h.dist < EPSILON)
            return result(h.color, t, h.specular);

        // NOTE: An extra step per voxel, use if sdf discontinuous between voxels
        //       Could make this conditional by prefixing sth. like if(voxel_changed) // suggested by spalmer, see variant below
        // constrain step to voxels (voxel-snap ray-march plugin by jt, thanks to Shane for the idea!)
        {
            // NOTE: assuming unit grid
            // sgn(rd)*0.5 are the walls in ray direction, fract(p) - 0.5 is center of voxel
            vec2 sd = (sgn(rd.xy)*0.5 - (fract(p.xy) - 0.5))/rd.xy; // distances to voxel sides / walls
            vec2 n = step(sd.xy, sd.yx); // component true if corresponding wall is nearest (at most one component true) NOTE: originally I used lessThanEqual, min from fb39ca4/kzy then switched to step, min by iq
            float skip = dot(sd, n) + EPSILON; // distance to next voxel: sum up all components, weighted by the nearest flag (assuming only one component is true this selects the nearest component)
            h.dist = min(h.dist, skip); // constrain step to at most next block to handle sdf discontinuities between voxels
        }

        t += h.dist;
    }

    //return result(h.color, t, h.specular); // stop on running out of iterations
    return result(h.color, t1, h.specular); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
//       Thanks to spalmer for pointing that out.
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).dist < t1 ? 0.0 : 1.0;
}
#ifdef HIGH_QUALITY
// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).dist;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}
#endif
// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).dist;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

float checker(vec2 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi));
}

float checker(vec3 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi)*sin(p.z*pi));
}

vec3 material(vec3 p)
{
    return vec3(1.0);
    //return vec3(hash13(p*123.456));
    //return vec3(hash33(p*123.456));
    //return mix(vec3(0.25), vec3(0.75), checker(p*2.0+0.5));
}

void mainImage(out vec4 fragColor, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0,-7.5);
    vec3 rd = normalize(vec3(I.xy, 2.0));

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;
    ro.z += 1.0;

    //vec3 sky_color = mix(vec3(0,0.5,0.5),vec3(0,0,1),abs(-rd.z));
    vec3 sky_color = mix(vec3(0,0.25,0.5),vec3(0,0,1),abs(-rd.z));
    vec3 color = vec3(1);
    result r = trace(ro, rd, 0.0, DIST_MAX);
    if(r.dist < DIST_MAX)
    {
        color *= r.color;
        vec3 dst = ro + rd * r.dist;
        vec3 n = normal(dst);

        //color *= (n * 0.5 + 0.5);
        color *= material(dst);

        //vec3 lightdir = normalize(vec3(3.0, 2.0, 1.0));
        vec3 lightdir = normalize(vec3(5.0, 3.0, 1.0));
        vec3 ambient = vec3(0.2);
        float brightness = max(dot(lightdir, n), 0.0) * 3.0;
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * r.dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * r.dist + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0); // requires HIGH_QUALITY
        color *= (ambient * calcAO(dst, n) + brightness);

        if(brightness > 0.0 && r.specular)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }

        //vec3 fog_color = vec3(0.2);
        vec3 fog_color = sky_color;
        color = mix(fog_color, vec3(color), exp(-pow(r.dist/20.0, 2.0))); // fog
    }
    else
    {
        color *= sky_color;
    }

    color = tanh(color); // roll-off overly bright colors
    fragColor = vec4(color, 1);
    fragColor = sqrt(fragColor); // approximate gamma
}
