void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Auflösung anpassen
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Parameter für die Spirale
    float phi = 1.61803398875; // Goldener Schnitt
    float a = 0.005; // Skalierungsfaktor der Spirale
    float b = log(phi); // Eine Näherung von log(phi) für die logarithmische Spirale

    // Berechnung des Abstandes zum Ursprung
    float r = length(uv);
    float angle = .9 * atan(uv.y, uv.x);

    // Logarithmische Spirale
    float spiral = exp(b * angle) * 0.1;

    // Einfache Darstellung in Schwarz und Weiß
    float lineWidth = 0.005; // Breite der Spirallinie
    float dist = abs(r - spiral);
    //float color = smoothstep(lineWidth, 0.0, dist);
    //float color = dist;
    vec3 color = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float goldenSpiral = smoothstep(lineWidth, 0.0, dist);

    fragColor = vec4(color * goldenSpiral, 1.0);
}
