// Input do ShaderToy
// iChannel0: VÃ­deo com fundo verde
// iChannel1: Imagem de fundo

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;

    vec4 video = texture(iChannel0, uv);
    vec4 background = texture(iChannel1, uv);


    vec3 keyColor = vec3(0.0, 1.0, 0.0);

    float tolerance = 0.7;

    float diff = length(video.rgb - keyColor);

    float mask = smoothstep(tolerance, tolerance + 0.1, diff);

    fragColor = mix(background, video, mask);
}
