#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define hmod(u, q) mod(u, 2. * q) - q
#define scr(p) rot(t) *  scale * (p.xy - r / 2.) / r.y

void mainImage(out vec4 o, vec2 u){
    vec2 A, B, a, b, M, H, W,
         r = iResolution.xy,
         q = vec2(sqrt(3.), 1);

    float t = iTime * .4,
          scale = cos(t) * 4. + 10.,
          hex   = sqrt(3.) / 2.,
          px    = 2. * scale / r.y;

    M = scr(iMouse);
    u = scr(u);

    A = hmod(u    , q);
    B = hmod(u + q, q);

    M.x /= hex;

        W = vec2(0, round(M.x / 2.));

        H = round((M + W) / 2.) * 2. - W;

        dot(abs(M - H), vec2(3, 2)) > 4.
            ? H += sign(M - H) * vec2(2, 1)
            : H;

    H.x *= hex;

    o = vec4(1. - .53 * min(dot(A, A), dot(B, B)));
    o.yz -= .45 * smoothstep(px, .0, length(u - H) - .5);
}
