vec3 render(vec2 uv, float _t, float k)
{
    float t = _t;
    vec3 col = vec3(1.0);

    vec2 v = vec2(0);
    v.x += sin(t*1.5)*0.5 + cos(t+sin(t*100.0))*0.6;
    v.y += cos(t+t)*0.25 + sin(t*2.0-cos(t*2.0))*0.3;

    uv += v;

    float d = length(uv) - 0.00001;

    d = pow(d,0.1);
    col.x += abs(cos(1.0/(k+0.5)))*0.15;
    col.y += abs(tan(1.0/(k+0.1)))*0.15;
    col.z += abs(sin(1.0/(k+0.7)))*0.15;
    col *= d;

    col = 1.- (col);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)-.5;
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime;
    vec3 col = vec3(0);
    const int limit = 100;
    for(int i=0;i<limit;i++)
    {
        float k = float(i)*.035;
        col += render(uv, t-k, k);
    }
    col /= float(limit);
    col *= 8.;
    col *= pow(vec3(.4545),col);

    fragColor = vec4(col,1.0);
}
