float rand(vec2 c){
 	return fract(sin(dot(c ,vec2(2.141,131.234))) * 123.225);   
}

// From https://www.shadertoy.com/view/cdlSzB
vec3 pal( in float t )
{
    vec3 a = vec3(0.538, 0.298, 0.738), 
         b = vec3(-0.142, 0.588, 0.248), 
         c = vec3(-1.502, -0.832, -2.502),
         d = vec3(0.660, 1.198, -0.142);
    return a + b*cos( 2.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (2.*fragCoord - R)/R.y;

    float t = 2.*iTime, s = 30.;
    vec2 pos = .5*vec2(2.5*cos(t),sin(2.*t));

    uv = floor(s*uv)/s;

    float dist = min(length(uv-pos),length(-uv-pos)) - .1;

    if(dist > 0. && rand(uv*t+1.) <.91){
        fragColor = texture(iChannel0, fragCoord/R);
        return;
    }

    fragColor = vec4(vec3(step(0.,-dist)*pal(.7*t)),1.0);
}
