#define TWO_PI              6.28318530718
#define r(A)                mat2(cos(A), -sin(A), sin(A), cos(A))

//https://iquilezles.org/articles/palettes/
vec3 palette(vec2 uv) {
    float t = length(uv) - iTime * .5;

    vec3 a = vec3(.468, .358, .5);
    vec3 b = vec3(.448, .218, .388);
    vec3 d = vec3(.002, .168, .498);

    return a + b * cos(TWO_PI * (t + d));
}

float circle(vec2 uv, vec2 center, float r)
{
    float brightness = .008;
    float l = length(uv + center);

    return brightness / abs(sin(l - r));
}

float shape(vec2 uv, float size)
{
    float mask = .0;
    float time = iTime * .03;

    const float sides = 7.;

    for (float i = 0.0; i < sides; i++)
    {
        uv *= r(TWO_PI / sides);
        vec2 uvOrig = uv;

        mask += circle(uv, vec2(uv.x, uv.y + size * 1.), size * 1.);
        uv = -(uvOrig * r(-2.65 * time));
    }

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    fragColor = vec4(shape(uv, .9) * palette(uv), 1.);
}
