#define GRIDW 5
#define BLOCKW 5
#define ScreenXDivide 800.0f
#define ScreenYDivide 800.0f
#define SmallW 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = fragCoord/iResolution.xy;
    uv = fragCoord/iResolution.y;


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //vec3 col = texture(iChannel0, fragCoord/iResolution.xy).rgb;

    vec3 c2 =vec3(92.,88.,74.)/(255.f); 
    vec3 col = c2*0.5f;

    ivec2 id = ivec2(int(uv.x*ScreenXDivide),int(uv.y*ScreenYDivide));
    int gridW=GRIDW;
    int blockNum = gridW*BLOCKW;
    float k=0.f;
    if(id.x%gridW==0 || id.y%gridW==0)
    {
        k=1.0f;
    }
    int w=SmallW;
    if((id.x+w)%blockNum==0 || (id.x+gridW-w)%blockNum==0 
    ||(id.y+w)%blockNum==0 || (id.y+gridW-w)%blockNum==0 )
    {
        k=1.0f;
    }

    // Output to screen
    fragColor = vec4(mix(c2*0.5+col*0.5,col,k),1.0);
}
